import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [filters, setFilters] = useState({ name: "", email: "", address: "", role: "" });
  const [form, setForm] = useState({ name: "", email: "", address: "", password: "", role: "user" });
  const [storeForm, setStoreForm] = useState({ name: "", email: "", address: "", owner_id: "" });

  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = async () => {
    const [usersRes, storesRes, ratingsRes] = await Promise.all([
      axios.get("http://localhost:5000/api/users/all"),
      axios.get("http://localhost:5000/api/stores"),
      axios.get("http://localhost:5000/api/ratings"),
    ]);
    setUsers(usersRes.data);
    setStores(storesRes.data);
    setRatings(ratingsRes.data);
  };

  const filteredUsers = users.filter(
    u =>
      u.name.toLowerCase().includes(filters.name.toLowerCase()) &&
      u.email.toLowerCase().includes(filters.email.toLowerCase()) &&
      u.address.toLowerCase().includes(filters.address.toLowerCase()) &&
      u.role.toLowerCase().includes(filters.role.toLowerCase())
  );

  const handleUserCreate = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/api/users/signup", form);
    setForm({ name: "", email: "", address: "", password: "", role: "user" });
    fetchAll();
  };

  const handleStoreCreate = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/api/stores/create", storeForm);
    setStoreForm({ name: "", email: "", address: "", owner_id: "" });
    fetchAll();
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <p><strong>Total Users:</strong> {users.length}</p>
      <p><strong>Total Stores:</strong> {stores.length}</p>
      <p><strong>Total Ratings:</strong> {ratings.length}</p>

      <h3>Create New User</h3>
      <form onSubmit={handleUserCreate}>
        <input name="name" placeholder="Name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} required />
        <input name="email" placeholder="Email" value={form.email} onChange={e => setForm({ ...form, email: e.target.value })} required />
        <input name="address" placeholder="Address" value={form.address} onChange={e => setForm({ ...form, address: e.target.value })} required />
        <input name="password" type="password" placeholder="Password" value={form.password} onChange={e => setForm({ ...form, password: e.target.value })} required />
        <select value={form.role} onChange={e => setForm({ ...form, role: e.target.value })}>
          <option value="user">Normal User</option>
          <option value="owner">Store Owner</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit">Add User</button>
      </form>

      <h3>Create New Store</h3>
      <form onSubmit={handleStoreCreate}>
        <input name="name" placeholder="Store Name" value={storeForm.name} onChange={e => setStoreForm({ ...storeForm, name: e.target.value })} required />
        <input name="email" placeholder="Store Email" value={storeForm.email} onChange={e => setStoreForm({ ...storeForm, email: e.target.value })} required />
        <input name="address" placeholder="Address" value={storeForm.address} onChange={e => setStoreForm({ ...storeForm, address: e.target.value })} required />
        <input name="owner_id" placeholder="Owner ID" value={storeForm.owner_id} onChange={e => setStoreForm({ ...storeForm, owner_id: e.target.value })} required />
        <button type="submit">Add Store</button>
      </form>

      <h3>All Users (Filterable)</h3>
      <input placeholder="Filter by name" onChange={e => setFilters({ ...filters, name: e.target.value })} />
      <input placeholder="Filter by email" onChange={e => setFilters({ ...filters, email: e.target.value })} />
      <input placeholder="Filter by address" onChange={e => setFilters({ ...filters, address: e.target.value })} />
      <input placeholder="Filter by role" onChange={e => setFilters({ ...filters, role: e.target.value })} />

      <table border="1">
        <thead>
          <tr><th>Name</th><th>Email</th><th>Address</th><th>Role</th></tr>
        </thead>
        <tbody>
          {filteredUsers.map(u => (
            <tr key={u.id}>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.address}</td>
              <td>{u.role}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
