import React, { useEffect, useState } from "react";
import axios from "axios";

export default function StoreList() {
  const [stores, setStores] = useState([]);
  const [userRatings, setUserRatings] = useState({});
  const [filter, setFilter] = useState("");
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    fetchStores();
    fetchUserRatings();
  }, []);

  const fetchStores = async () => {
    const res = await axios.get("http://localhost:5000/api/stores");
    setStores(res.data);
  };

  const fetchUserRatings = async () => {
    const res = await axios.get(`http://localhost:5000/api/ratings/user/${user.id}`);
    const ratingsMap = {};
    res.data.forEach(r => {
      ratingsMap[r.store_id] = r.rating;
    });
    setUserRatings(ratingsMap);
  };

  const handleRating = async (store_id, rating) => {
    await axios.post("http://localhost:5000/api/ratings", {
      user_id: user.id,
      store_id,
      rating,
    });
    fetchUserRatings(); // Refresh rating
  };

  const filteredStores = stores.filter(store =>
    store.name.toLowerCase().includes(filter.toLowerCase()) ||
    store.address.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div>
      <h2>Stores</h2>
      <input
        placeholder="Search by name or address"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
      />
      <table border="1">
        <thead>
          <tr>
            <th>Store Name</th>
            <th>Address</th>
            <th>Average Rating</th>
            <th>Your Rating</th>
            <th>Rate</th>
          </tr>
        </thead>
        <tbody>
          {filteredStores.map((store) => (
            <tr key={store.id}>
              <td>{store.name}</td>
              <td>{store.address}</td>
              <td>{parseFloat(store.average_rating).toFixed(1) || "No ratings yet"}</td>
              <td>{userRatings[store.id] || "Not rated"}</td>
              <td>
                {[1, 2, 3, 4, 5].map((star) => (
                  <button
                    key={star}
                    onClick={() => handleRating(store.id, star)}
                    style={{
                      fontWeight: userRatings[store.id] === star ? "bold" : "normal",
                      margin: "2px",
                    }}
                  >
                    {star}
                  </button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
