import React, { useState } from "react";
import axios from "axios";

export default function Signup() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    role: "user",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/users/signup", form);
      alert("Signup successful!");
    } catch (err) {
      alert("Signup failed.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" placeholder="Name" minLength={20} maxLength={60} required onChange={handleChange} />
      <input name="email" placeholder="Email" type="email" required onChange={handleChange} />
      <input name="address" placeholder="Address" maxLength={400} required onChange={handleChange} />
      <input name="password" placeholder="Password" type="password" pattern="(?=.*[A-Z])(?=.*[^a-zA-Z0-9]).{8,16}" required onChange={handleChange} />
      <select name="role" onChange={handleChange}>
        <option value="user">Normal User</option>
        <option value="owner">Store Owner</option>
        <option value="admin">Admin</option>
      </select>
      <button type="submit">Sign Up</button>
    </form>
  );
}
