import React, { useState } from "react";
import axios from "axios";
import "../styles/AuthForm.css"; // ✅ Reuse shared CSS styling

export default function Signup() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    role: "user",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/users/signup", form);
      alert("Signup successful!");
    } catch (err) {
      alert("Signup failed.");
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={handleSubmit}>
        <h2 className="auth-title">Sign Up</h2>
        <input
          name="name"
          placeholder="Name"
          minLength={20}
          maxLength={60}
          required
          onChange={handleChange}
          className="auth-input"
        />
        <input
          name="email"
          placeholder="Email"
          type="email"
          required
          onChange={handleChange}
          className="auth-input"
        />
        <input
          name="address"
          placeholder="Address"
          maxLength={400}
          required
          onChange={handleChange}
          className="auth-input"
        />
        <input
          name="password"
          placeholder="Password"
          type="password"
          pattern="(?=.*[A-Z])(?=.*[^a-zA-Z0-9]).{8,16}"
          title="Password must be 8-16 characters, include one uppercase letter and one special character"
          required
          onChange={handleChange}
          className="auth-input"
        />
        <select name="role" onChange={handleChange} className="auth-input">
          <option value="user">Normal User</option>
          <option value="owner">Store Owner</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit" className="auth-button">Sign Up</button>
        <p className="auth-link-text">
          Already have an account? <a href="/login" className="auth-link">Login</a>
        </p>
      </form>
    </div>
  );
}
