import React, { useEffect, useState } from "react";
import axios from "axios";

export default function OwnerDashboard() {
  const [store, setStore] = useState(null);
  const [ratings, setRatings] = useState([]);
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    fetchStoreAndRatings();
  }, []);

  const fetchStoreAndRatings = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/stores");
      const ownerStore = res.data.find((s) => s.owner_id === user.id);

      if (ownerStore) {
        setStore(ownerStore);
        const ratingsRes = await axios.get(`http://localhost:5000/api/ratings/store/${ownerStore.id}`);
        setRatings(ratingsRes.data);
      }
    } catch (err) {
      console.error("Failed to load store or ratings");
    }
  };

  const averageRating = ratings.length
    ? (ratings.reduce((acc, r) => acc + r.rating, 0) / ratings.length).toFixed(1)
    : "N/A";

  return (
    <div>
      <h2>Store Owner Dashboard</h2>
      {store ? (
        <>
          <h3>Store: {store.name}</h3>
          <p><strong>Address:</strong> {store.address}</p>
          <p><strong>Average Rating:</strong> {averageRating}</p>
          <h4>User Ratings:</h4>
          {ratings.length > 0 ? (
            <table border="1">
              <thead>
                <tr><th>User Name</th><th>Rating</th></tr>
              </thead>
              <tbody>
                {ratings.map((r, i) => (
                  <tr key={i}>
                    <td>{r.name}</td>
                    <td>{r.rating}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No ratings yet</p>
          )}
        </>
      ) : (
        <p>You don't have a store assigned yet.</p>
      )}
    </div>
  );
}
